import React, { useState, useEffect } from 'react';

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [language, setLanguage] = useState('spanish');
  const [level, setLevel] = useState('novice');
  const [isLoading, setIsLoading] = useState(false);
  const [activeExercise, setActiveExercise] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);
  const [currentFeedback, setCurrentFeedback] = useState(null);
  const [progress, setProgress] = useState({
    totalSessions: 0,
    averageScore: 0,
    vocabularyMastered: 0,
    grammarPoints: 0
  });
  const [enableFeedback, setEnableFeedback] = useState(true); // New state for toggle

  const updateProgress = async (feedback) => {
    try {
      await fetch('http://localhost:8000/record-progress', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: 'demo-user',
          date: new Date().toISOString(),
          score: feedback.score,
          language: language,
          level: level,
          exercise_type: activeExercise?.type
        })
      });

      setProgress(prev => ({
        ...prev,
        totalSessions: prev.totalSessions + 1,
        averageScore: Math.round(
          (prev.averageScore * prev.totalSessions + feedback.score) / 
          (prev.totalSessions + 1)
        ),
        vocabularyMastered: prev.vocabularyMastered + 
          (feedback.suggestions.some(s => s.toLowerCase().includes('vocabulary')) ? 1 : 0),
        grammarPoints: prev.grammarPoints + 
          (feedback.errors.length > 0 ? 1 : 0)
      }));
    } catch (error) {
      console.error('Error recording progress:', error);
    }
  };

  useEffect(() => {
    if (currentFeedback) {
      updateProgress(currentFeedback);
    }
  }, [currentFeedback]);

  const handleSend = async () => {
    if (!inputText.trim()) return;

    if (!activeExercise && (inputText.toLowerCase().includes('vocab') || 
                           inputText.toLowerCase().includes('exercise') ||
                           inputText.toLowerCase().includes('practice'))) {
      handleGetExercise();
      return;
    }

    const userMessage = { 
      text: inputText, 
      sender: 'user',
      isExercise: !!activeExercise
    };
    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsLoading(true);
    
    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          text: inputText, 
          language, 
          level,
          is_exercise: !!activeExercise
        })
      });
      
      const data = await response.json();
      
      const tutorMessage = { 
        text: data.response, 
        sender: 'tutor',
        feedback: data.feedback
      };
      
      setMessages(prev => [...prev, tutorMessage]);
      
      if (data.feedback) {
        setCurrentFeedback({
          ...data.feedback,
          userInput: inputText
        });
        setShowFeedback(true);
      }
      
      if (activeExercise) {
        setActiveExercise(null);
      }
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, { 
        text: "Sorry, I encountered an error. Please try again.", 
        sender: 'tutor' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleGetExercise = async () => {
    setIsLoading(true);
    setMessages(prev => [...prev, { 
      text: "Great! Let's practice some vocabulary...", 
      sender: 'tutor' 
    }]);
    
    try {
      const response = await fetch('http://localhost:8000/vocab-exercise', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ language, level })
      });
      
      const data = await response.json();
      setActiveExercise(data);
      
      const exerciseMessage = { 
        text: data.content || data.raw, 
        sender: 'exercise',
        exerciseType: data.type,
        instructions: data.instructions,
        targetAnswers: data.target
      };
      
      setMessages(prev => [...prev, exerciseMessage]);
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, { 
        text: "Sorry, I couldn't generate an exercise right now.", 
        sender: 'tutor' 
      }]);
    } finally {
      setIsLoading(false);
      setInputText('');
    }
  };

  const renderLanguageText = (text) => {
    return text.split('\n').map((paragraph, pIndex) => {
      // Simple detection for Spanish/Urdu vs English
      const isTargetLanguage = /[à-úÀ-ÚñÑ]/.test(paragraph) || 
                             (language === 'urdu' && /[a-zA-Z]/.test(paragraph));
      
      return (
        <p 
          key={pIndex} 
          className={isTargetLanguage ? 'target-language' : 'english-text'}
          lang={isTargetLanguage ? language : 'en'}
        >
          {paragraph}
        </p>
      );
    });
  };

  const ProgressDisplay = () => (
    <div className="progress-display">
      <h3>Your Learning Progress</h3>
      <div className="progress-metrics">
        <div className="progress-item">
          <span className="progress-value">{progress.totalSessions}</span>
          <span className="progress-label">Sessions</span>
        </div>
        <div className="progress-item">
          <span className="progress-value">{progress.averageScore}%</span>
          <span className="progress-label">Accuracy</span>
        </div>
        <div className="progress-item">
          <span className="progress-value">{progress.vocabularyMastered}</span>
          <span className="progress-label">Words Learned</span>
        </div>
      </div>
    </div>
  );

  const renderFeedbackModal = () => {
    if (!showFeedback || !currentFeedback) return null;
    
    return (
      <div className="feedback-modal">
        <div className="feedback-content">
          <h3>Detailed Feedback</h3>
          
          <div className="feedback-score">
            <strong>Performance:</strong> 
            <span className={`score-${Math.floor(currentFeedback.score/20)}`}>
              {currentFeedback.score}/100
            </span>
          </div>
          
          <div className="feedback-section">
            <h4>Your Input:</h4>
            <p className="user-input">{currentFeedback.userInput}</p>
          </div>
          
          {currentFeedback.corrected && (
            <div className="feedback-section">
              <h4>Corrected Version:</h4>
              <p className="corrected-text">{currentFeedback.corrected}</p>
            </div>
          )}
          
          {currentFeedback.errors?.length > 0 && (
            <div className="feedback-section">
              <h4>Errors Found:</h4>
              <ul className="error-list">
                {currentFeedback.errors.map((error, index) => (
                  <li key={index}>{error}</li>
                ))}
              </ul>
            </div>
          )}
          
          {currentFeedback.explanation && (
            <div className="feedback-section">
              <h4>Explanation:</h4>
              <p className="explanation-text">{currentFeedback.explanation}</p>
            </div>
          )}
          
          {currentFeedback.suggestions?.length > 0 && (
            <div className="feedback-section">
              <h4>Suggestions for Improvement:</h4>
              <ul className="suggestions-list">
                {currentFeedback.suggestions.map((suggestion, index) => (
                  <li key={index}>{suggestion}</li>
                ))}
              </ul>
            </div>
          )}
          
          <button 
            onClick={() => setShowFeedback(false)}
            className="close-feedback"
          >
            Close
          </button>
        </div>
      </div>
    );
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <div className="chat-container">
      <ProgressDisplay />
      {renderFeedbackModal()}
      
      <div className="controls">
        <select 
          value={language} 
          onChange={(e) => setLanguage(e.target.value)}
          disabled={isLoading}
        >
          <option value="spanish">Spanish</option>
          <option value="urdu">Urdu</option>
        </select>
        
        <select 
          value={level} 
          onChange={(e) => setLevel(e.target.value)}
          disabled={isLoading}
        >
          <option value="novice">Novice</option>
          <option value="beginner">Beginner</option>
          <option value="intermediate">Intermediate</option>
          <option value="advanced">Advanced</option>
        </select>
        
        <button 
          onClick={handleGetExercise} 
          disabled={isLoading || activeExercise}
          className="exercise-btn"
        >
          Get Exercise
        </button>
      </div>
      
      <div className="messages">
        {messages.map((msg, i) => (
          <div key={i} className={`message ${msg.sender}`}>
            {msg.sender === 'exercise' && (
              <div className="exercise-header">
                <span className="exercise-badge">{msg.exerciseType.toLowerCase()}</span>
                <span className="exercise-difficulty">{level}</span>
              </div>
            )}
            {msg.instructions && (
              <div className="exercise-instructions">
                <strong>Instructions:</strong> {msg.instructions}
              </div>
            )}
            <div className="message-content">
              {renderLanguageText(msg.text)}
            </div>
            {msg.feedback && (
              <button 
                onClick={() => {
                  setCurrentFeedback({
                    ...msg.feedback,
                    userInput: messages.find(m => m.sender === 'user' && m.isExercise === !!msg.feedback)?.text || ''
                  });
                  setShowFeedback(true);
                }}
                className="view-feedback-btn"
              >
                View Detailed Feedback
              </button>
            )}
          </div>
        ))}
        
        {isLoading && (
          <div className="message tutor">
            <div className="typing-indicator">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        )}
      </div>
      
      <div className="input-area">
        <input
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder={
            activeExercise 
              ? "Type your answer here..." 
              : "Type your message or ask for 'vocab exercise'..."
          }
          disabled={isLoading}
        />
        <button 
          onClick={handleSend} 
          disabled={isLoading || !inputText.trim()}
        >
          {activeExercise ? "Submit Answer" : "Send"}
        </button>
      </div>
    </div>
  );
}

export default ChatInterface;
