import React, { useState, useEffect } from 'react';

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [language, setLanguage] = useState('spanish');
  const [level, setLevel] = useState('beginner');
  const [isLoading, setIsLoading] = useState(false);
  const [showExercise, setShowExercise] = useState(false);
  const [currentExercise, setCurrentExercise] = useState(null);
  const [exerciseAnswer, setExerciseAnswer] = useState('');

  // Add to handleSend function (before the try block)
  if (inputText.toLowerCase().includes('vocab') || inputText.toLowerCase().includes('exercise')) {
    handleGetExercise();
    return;
  }

  const handleGetExercise = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:8000/vocab-exercise', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ language, level })
      });
      
      const data = await response.json();
      setCurrentExercise(data);
      setShowExercise(true);
      setMessages(prev => [...prev, 
        { text: "Let's practice some vocabulary!", sender: 'tutor' },
        { text: data.exercise, sender: 'exercise' }
      ]);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
      setInputText('');
    }
  };

  const handleExerciseSubmit = async () => {
    if (!exerciseAnswer.trim()) return;
    
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          text: `Please check my answer for the ${currentExercise.type} exercise: ${exerciseAnswer}`,
          language, 
          level 
        })
      });
      
      const data = await response.json();
      setMessages(prev => [...prev, 
        { text: `My answer: ${exerciseAnswer}`, sender: 'user' },
        { text: data.response, sender: 'tutor' }
      ]);
      setShowExercise(false);
      setExerciseAnswer('');
      setCurrentExercise(null);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Update the return statement to include exercise UI
  return (
    <div className="chat-container">
      <div className="controls">
        <select value={language} onChange={(e) => setLanguage(e.target.value)}>
          <option value="spanish">Spanish</option>
        </select>
        <select value={level} onChange={(e) => setLevel(e.target.value)}>
          <option value="beginner">Beginner</option>
          <option value="intermediate">Intermediate</option>
          <option value="advanced">Advanced</option>
        </select>
        <button onClick={handleGetExercise} disabled={isLoading}>
          Get Exercise
        </button>
      </div>
      
      <div className="messages">
        {messages.map((msg, i) => (
          <div key={i} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
        {isLoading && <div className="message tutor">Thinking...</div>}
      </div>
      
      {showExercise ? (
        <div className="exercise-area">
          <h3>Vocabulary Exercise ({currentExercise.type})</h3>
          <div className="exercise-content">
            {currentExercise.exercise}
          </div>
          <input
            value={exerciseAnswer}
            onChange={(e) => setExerciseAnswer(e.target.value)}
            placeholder="Your answer..."
          />
          <button onClick={handleExerciseSubmit} disabled={isLoading}>
            Submit Answer
          </button>
        </div>
      ) : (
        <div className="input-area">
          <input
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Type your message or ask for 'vocab exercise'..."
          />
          <button onClick={handleSend} disabled={isLoading}>
            Send
          </button>
        </div>
      )}
    </div>
  );
}
