import React, { useState, useEffect } from 'react';
import './styles.css';

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [language, setLanguage] = useState('spanish');
  const [level, setLevel] = useState('novice');
  const [isLoading, setIsLoading] = useState(false);
  const [activeExercise, setActiveExercise] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);
  const [currentFeedback, setCurrentFeedback] = useState(null);
  const [progress, setProgress] = useState({
    totalSessions: 0,
    averageScore: 0,
    vocabularyMastered: 0,
  });
  const [enableFeedback, setEnableFeedback] = useState(true);
  const [mode, setMode] = useState('conversation'); // 'conversation' or 'exercise'

  const updateProgress = async (score) => {
    setProgress(prev => ({
      ...prev,
      totalSessions: prev.totalSessions + 1,
      averageScore: Math.round((prev.averageScore * prev.totalSessions + score) / (prev.totalSessions + 1)),
      vocabularyMastered: prev.vocabularyMastered + (score === 100 ? 1 : 0),
    }));
  };

  const handleSend = async () => {
    if (!inputText.trim()) return;

    // Exercise Mode: Submit answer for grading
    if (mode === 'exercise' && activeExercise) {
      const userMessage = { text: inputText, sender: 'user', isExercise: true };
      setMessages(prev => [...prev, userMessage]);
      setInputText('');
      setIsLoading(true);

      try {
        const response = await fetch('http://localhost:8000/grade-exercise', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            user_answer: inputText,
            exercise_type: activeExercise.type,
            target_answer: activeExercise.target,
            language: language,
            level: level
          })
        });

        const result = await response.json();
        updateProgress(result.score);

        setMessages(prev => [...prev, {
          text: result.feedback,
          sender: 'tutor',
          isGraded: true,
          score: result.score
        }]);
      } catch (error) {
        console.error('Error:', error);
      } finally {
        setIsLoading(false);
        setActiveExercise(null);
      }
      return;
    }

    // Conversation Mode: Original chat logic
    const userMessage = { text: inputText, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          text: inputText, 
          language, 
          level,
          enable_feedback: enableFeedback
        })
      });
      const data = await response.json();
      setMessages(prev => [...prev, { text: data.response, sender: 'tutor' }]);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleGetExercise = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:8000/vocab-exercise', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ language, level })
      });
      const data = await response.json();
      setActiveExercise(data);
      setMessages(prev => [...prev, { 
        text: data.content, 
        sender: 'exercise',
        exerciseType: data.type,
        instructions: data.instructions
      }]);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chat-container">
      <div className="mode-toggle">
        <button 
          onClick={() => setMode('conversation')} 
          className={mode === 'conversation' ? 'active' : ''}
        >
          Conversation
        </button>
        <button 
          onClick={() => setMode('exercise')} 
          className={mode === 'exercise' ? 'active' : ''}
        >
          Exercises
        </button>
      </div>

      <div className="controls">
        <select value={language} onChange={(e) => setLanguage(e.target.value)}>
          <option value="spanish">Spanish</option>
          <option value="urdu">Urdu</option>
        </select>
        <select value={level} onChange={(e) => setLevel(e.target.value)}>
          <option value="novice">Novice</option>
          <option value="beginner">Beginner</option>
          <option value="intermediate">Intermediate</option>
          <option value="advanced">Advanced</option>
        </select>
        {mode === 'exercise' && (
          <button onClick={handleGetExercise} disabled={isLoading || activeExercise}>
            Get Exercise
          </button>
        )}
      </div>

      <div className="messages">
        {messages.map((msg, i) => (
          <div key={i} className={`message ${msg.sender}`}>
            {msg.sender === 'exercise' && (
              <div className="exercise-header">
                <span className="exercise-badge">{msg.exerciseType}</span>
              </div>
            )}
            {msg.instructions && (
              <div className="exercise-instructions">{msg.instructions}</div>
            )}
            <div className="message-content">{msg.text}</div>
            {msg.isGraded && (
              <div className="score-badge">Score: {msg.score}/100</div>
            )}
          </div>
        ))}
        {isLoading && (
          <div className="message tutor">
            <div className="typing-indicator">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        )}
      </div>

      <div className="input-area">
        <input
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          placeholder={
            mode === 'exercise' && activeExercise 
              ? "Type your answer..." 
              : "Type a message..."
          }
          disabled={isLoading}
        />
        <button onClick={handleSend} disabled={isLoading || !inputText.trim()}>
          {mode === 'exercise' && activeExercise ? "Submit" : "Send"}
        </button>
      </div>
    </div>
  );
}

export default ChatInterface;
