import React, { useState, useEffect } from 'react';
import './styles.css';

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [language, setLanguage] = useState('spanish');
  const [level, setLevel] = useState('novice');
  const [isLoading, setIsLoading] = useState(false);
  const [activeExercise, setActiveExercise] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);
  const [currentFeedback, setCurrentFeedback] = useState(null);
  const [enableFeedback, setEnableFeedback] = useState(true);
  const [progress, setProgress] = useState({
    totalSessions: 0,
    averageScore: 0,
    vocabularyMastered: 0
  });

  const updateProgress = async (feedback) => {
    if (!enableFeedback) return;
    
    try {
      await fetch('http://localhost:8000/record-progress', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: 'demo-user',
          date: new Date().toISOString(),
          score: feedback.score,
          language,
          level,
          exercise_type: activeExercise?.type
        })
      });

      setProgress(prev => ({
        ...prev,
        totalSessions: prev.totalSessions + 1,
        averageScore: Math.round(
          (prev.averageScore * prev.totalSessions + feedback.score) / 
          (prev.totalSessions + 1)
        ),
        vocabularyMastered: prev.vocabularyMastered + 
          (feedback.suggestions.some(s => s.includes('vocabulary')) ? 1 : 0)
      }));
    } catch (error) {
      console.error('Progress update failed:', error);
    }
  };

  const handleSend = async () => {
    if (!inputText.trim()) return;

    const userMessage = { 
      text: inputText, 
      sender: 'user',
      isExercise: !!activeExercise
    };
    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsLoading(true);
    
    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          text: inputText,
          language, 
          level,
          is_exercise: !!activeExercise,
          enable_feedback: enableFeedback
        })
      });
      
      const data = await response.json();
      
      const tutorMessage = { 
        text: data.response, 
        sender: 'tutor',
        feedback: enableFeedback ? data.feedback : null
      };
      
      setMessages(prev => [...prev, tutorMessage]);
      
      if (enableFeedback && data.feedback) {
        setCurrentFeedback(data.feedback);
        setShowFeedback(true);
        updateProgress(data.feedback);
      }
      
      if (activeExercise) setActiveExercise(null);
    } catch (error) {
      console.error('Chat error:', error);
      setMessages(prev => [...prev, { 
        text: language === 'spanish' 
          ? "Lo siento, hubo un error. Por favor intenta nuevamente." 
          : "Sorry, an error occurred. Please try again.", 
        sender: 'tutor' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleGetExercise = async () => {
    setIsLoading(true);
    setMessages(prev => [...prev, { 
      text: language === 'spanish' 
        ? "¡Vamos a practicar vocabulario!" 
        : "Let's practice vocabulary!", 
      sender: 'tutor' 
    }]);
    
    try {
      const response = await fetch('http://localhost:8000/vocab-exercise', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ language, level })
      });
      
      const data = await response.json();
      setActiveExercise(data);
      
      setMessages(prev => [...prev, { 
        text: data.content, 
        sender: 'exercise',
        instructions: data.instructions,
        targetAnswers: data.target
      }]);
    } catch (error) {
      console.error('Exercise error:', error);
      setMessages(prev => [...prev, { 
        text: language === 'spanish' 
          ? "No pude generar un ejercicio ahora." 
          : "Couldn't generate an exercise now.", 
        sender: 'tutor' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  const renderMessageContent = (text) => {
    return text.split('\n').map((paragraph, i) => (
      <p key={i} className={language === 'spanish' && /[áéíóúñ¿¡]/i.test(paragraph) 
        ? 'target-language' 
        : 'english-text'}>
        {paragraph}
      </p>
    ));
  };

  return (
    <div className="chat-container">
      <div className="settings-bar">
        <label className="feedback-toggle">
          <input
            type="checkbox"
            checked={enableFeedback}
            onChange={() => setEnableFeedback(!enableFeedback)}
          />
          <span>{enableFeedback ? "Disable" : "Enable"} Feedback</span>
        </label>
      </div>

      <div className="controls">
        <select value={language} onChange={(e) => setLanguage(e.target.value)}>
          <option value="spanish">Spanish</option>
          <option value="urdu">Urdu</option>
        </select>
        
        <select value={level} onChange={(e) => setLevel(e.target.value)}>
          <option value="novice">Novice</option>
          <option value="beginner">Beginner</option>
          <option value="intermediate">Intermediate</option>
          <option value="advanced">Advanced</option>
        </select>
        
        <button 
          onClick={handleGetExercise}
          disabled={isLoading || activeExercise}
        >
          {language === 'spanish' ? "Ejercicio" : "Exercise"}
        </button>
      </div>

      <div className="messages">
        {messages.map((msg, i) => (
          <div key={i} className={`message ${msg.sender}`}>
            {msg.sender === 'exercise' && (
              <div className="exercise-header">
                <span className="exercise-difficulty">{level}</span>
              </div>
            )}
            <div className="message-content">
              {renderMessageContent(msg.text)}
            </div>
            {msg.feedback && (
              <button 
                onClick={() => {
                  setCurrentFeedback(msg.feedback);
                  setShowFeedback(true);
                }}
                className="view-feedback-btn"
              >
                {language === 'spanish' ? "Ver retroalimentación" : "View Feedback"}
              </button>
            )}
          </div>
        ))}
        {isLoading && (
          <div className="message tutor">
            <div className="typing-indicator">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        )}
      </div>

      <div className="input-area">
        <input
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          placeholder={
            activeExercise
              ? language === 'spanish'
                ? "Escribe tu respuesta..."
                : "Type your answer..."
              : language === 'spanish'
                ? "Escribe tu mensaje..."
                : "Type your message..."
          }
          disabled={isLoading}
        />
        <button 
          onClick={handleSend}
          disabled={isLoading || !inputText.trim()}
        >
          {activeExercise 
            ? language === 'spanish' ? "Enviar" : "Submit"
            : language === 'spanish' ? "Enviar" : "Send"}
        </button>
      </div>
    </div>
  );
}

export default ChatInterface;
