// ChatInterface.js

import React, { useState } from "react";
import axios from "axios";

const API_BASE = "http://localhost:8000";

export default function ChatInterface() {
  const [input, setInput] = useState("");
  const [level, setLevel] = useState("beginner");
  const [language, setLanguage] = useState("spanish");
  const [isExercise, setIsExercise] = useState(false);
  const [chatLog, setChatLog] = useState([]);
  const [exercise, setExercise] = useState(null);
  const [exerciseAnswer, setExerciseAnswer] = useState("");
  const [exerciseFeedback, setExerciseFeedback] = useState("");

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { role: "user", text: input };
    setChatLog(prev => [...prev, userMsg]);
    setInput("");

    try {
      const res = await axios.post(`${API_BASE}/chat`, {
        text: input,
        level,
        language,
        is_exercise: isExercise,
      });

      const aiMsg = {
        role: "tutor",
        text: res.data.response,
        feedback: res.data.feedback,
      };
      setChatLog(prev => [...prev, aiMsg]);
    } catch (err) {
      console.error("Chat error:", err);
      setChatLog(prev => [...prev, { role: "tutor", text: "Oops! Try again." }]);
    }
  };

  const fetchExercise = async () => {
    try {
      const res = await axios.post(`${API_BASE}/vocab-exercise`, {
        language,
        level,
      });
      setExercise(res.data);
      setExerciseAnswer("");
      setExerciseFeedback("");
    } catch (err) {
      console.error("Exercise error:", err);
    }
  };

  const checkExerciseAnswer = () => {
    if (!exercise || !exercise.target?.length) return;
    const correctSet = new Set(exercise.target.map(w => w.toLowerCase()));
    const userSet = new Set(
      exerciseAnswer
        .split(",")
        .map(w => w.trim().toLowerCase())
        .filter(Boolean)
    );
    const correct = [...userSet].filter(w => correctSet.has(w));
    const incorrect = [...userSet].filter(w => !correctSet.has(w));

    const feedback = `✅ Correct: ${correct.join(", ") || "None"}\n❌ Incorrect: ${incorrect.join(", ") || "None"}`;
    setExerciseFeedback(feedback);
  };

  return (
    <div style={{ maxWidth: 600, margin: "0 auto", padding: 20 }}>
      <h2>Language Tutor Assistant</h2>

      <div style={{ marginBottom: 10 }}>
        <label>Level: </label>
        <select value={level} onChange={e => setLevel(e.target.value)}>
          <option value="beginner">Beginner</option>
          <option value="intermediate">Intermediate</option>
          <option value="advanced">Advanced</option>
        </select>
        <label style={{ marginLeft: 10 }}>Language: </label>
        <select value={language} onChange={e => setLanguage(e.target.value)}>
          <option value="spanish">Spanish</option>
        </select>
      </div>

      <div>
        <textarea
          rows={3}
          placeholder="Type a message..."
          value={input}
          onChange={e => setInput(e.target.value)}
          style={{ width: "100%", padding: 8 }}
        />
        <div style={{ marginTop: 10 }}>
          <button onClick={sendMessage}>Send</button>
          <label style={{ marginLeft: 10 }}>
            <input
              type="checkbox"
              checked={isExercise}
              onChange={() => setIsExercise(prev => !prev)}
            />
            Mark as exercise
          </label>
          <button onClick={fetchExercise} style={{ float: "right" }}>
            Vocab Practice
          </button>
        </div>
      </div>

      <div style={{ marginTop: 20 }}>
        <h4>Chat Log</h4>
        <div>
          {chatLog.map((entry, idx) => (
            <div key={idx} style={{ marginBottom: 12 }}>
              <strong>{entry.role === "user" ? "You" : "Tutor"}:</strong>
              <p>{entry.text}</p>
              {entry.role === "tutor" && entry.feedback && (
                <div style={{ background: "#f5f5f5", padding: 8, borderRadius: 4 }}>
                  <strong>Feedback:</strong>
                  <ul>
                    <li>Score: {entry.feedback.score}</li>
                    <li>Errors: {entry.feedback.errors.join(", ")}</li>
                    <li>Corrected: {entry.feedback.corrected}</li>
                    <li>Explanation: {entry.feedback.explanation}</li>
                    <li>Suggestions: {entry.feedback.suggestions.join(", ")}</li>
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {exercise && (
        <div style={{ marginTop: 30, borderTop: "1px solid #ccc", paddingTop: 20 }}>
          <h4>📘 Vocabulary Exercise ({exercise.type})</h4>
          <p><strong>Instructions:</strong> {exercise.instructions}</p>
          <pre>{exercise.content}</pre>
          <input
            type="text"
            placeholder="Your answer (comma separated)"
            value={exerciseAnswer}
            onChange={e => setExerciseAnswer(e.target.value)}
            style={{ width: "100%", marginBottom: 10 }}
          />
          <button onClick={checkExerciseAnswer}>Check Answer</button>
          {exerciseFeedback && (
            <pre style={{ marginTop: 10 }}>{exerciseFeedback}</pre>
          )}
        </div>
      )}
    </div>
  );
}
