import React, { useState } from 'react';

function ChatInterface() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [language, setLanguage] = useState('spanish');
  const [level, setLevel] = useState('beginner');
  const [isLoading, setIsLoading] = useState(false);
  const [activeExercise, setActiveExercise] = useState(null);
  const [exerciseAnswer, setExerciseAnswer] = useState('');

  const handleSend = async () => {
    if (!inputText.trim()) return;
    
    // Check if user is requesting an exercise
    if (inputText.toLowerCase().includes('vocab') || 
        inputText.toLowerCase().includes('exercise') ||
        inputText.toLowerCase().includes('practice')) {
      handleGetExercise();
      return;
    }

    // Handle exercise submission if active
    if (activeExercise) {
      await handleExerciseSubmit(inputText);
      return;
    }

    // Regular chat message
    const userMessage = { text: inputText, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsLoading(true);
    
    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: inputText, language, level })
      });
      
      const data = await response.json();
      setMessages(prev => [...prev, { text: data.response, sender: 'tutor' }]);
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, { 
        text: "Sorry, I encountered an error. Please try again.", 
        sender: 'tutor' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleGetExercise = async () => {
    setIsLoading(true);
    setMessages(prev => [...prev, { 
      text: "Great! Let's practice some vocabulary...", 
      sender: 'tutor' 
    }]);
    
    try {
      const response = await fetch('http://localhost:8000/vocab-exercise', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ language, level })
      });
      
      const data = await response.json();
      setActiveExercise(data);
      setMessages(prev => [...prev, { 
        text: data.exercise, 
        sender: 'exercise',
        exerciseType: data.type
      }]);
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, { 
        text: "Sorry, I couldn't generate an exercise right now.", 
        sender: 'tutor' 
      }]);
    } finally {
      setIsLoading(false);
      setInputText('');
    }
  };

  const handleExerciseSubmit = async (answer) => {
    if (!answer.trim()) return;
    
    setIsLoading(true);
    setMessages(prev => [...prev, { 
      text: `My answer: ${answer}`, 
      sender: 'user' 
    }]);
    
    try {
      const response = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          text: `Please evaluate my ${activeExercise.type} exercise answer: ${answer}`,
          language, 
          level 
        })
      });
      
      const data = await response.json();
      setMessages(prev => [...prev, { 
        text: data.response, 
        sender: 'tutor' 
      }]);
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, { 
        text: "Sorry, I couldn't evaluate your answer right now.", 
        sender: 'tutor' 
      }]);
    } finally {
      setIsLoading(false);
      setActiveExercise(null);
      setExerciseAnswer('');
      setInputText('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <div className="chat-container">
      <div className="controls">
        <select 
          value={language} 
          onChange={(e) => setLanguage(e.target.value)}
          disabled={isLoading}
        >
          <option value="spanish">Spanish</option>
          {/* Add more languages as needed */}
        </select>
        
        <select 
          value={level} 
          onChange={(e) => setLevel(e.target.value)}
          disabled={isLoading}
        >
          <option value="beginner">Beginner</option>
          <option value="intermediate">Intermediate</option>
          <option value="advanced">Advanced</option>
        </select>
        
        <button 
          onClick={handleGetExercise} 
          disabled={isLoading}
          className="exercise-btn"
        >
          Get Vocabulary Exercise
        </button>
      </div>
      
      <div className="messages">
        {messages.map((msg, i) => (
          <div key={i} className={`message ${msg.sender}`}>
            {msg.text}
            {msg.exerciseType && (
              <div className="exercise-tag">{msg.exerciseType.toLowerCase()}</div>
            )}
          </div>
        ))}
        
        {isLoading && (
          <div className="message tutor">
            <div className="typing-indicator">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        )}
      </div>
      
      <div className="input-area">
        <input
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder={
            activeExercise 
              ? "Type your answer here..." 
              : "Type your message or ask for 'vocab exercise'..."
          }
          disabled={isLoading}
        />
        <button 
          onClick={handleSend} 
          disabled={isLoading || !inputText.trim()}
        >
          {activeExercise ? "Submit Answer" : "Send"}
        </button>
      </div>
    </div>
  );
}

export default ChatInterface;
